import React from 'react';
import PropTypes from 'prop-types';
import './Review.css';
import { Link } from 'react-router-dom';


function Review({ id, owner_id, yn_reply, review_title, modified_day, review_body, yn_visible }) {
  return (
    <div className="review">
          <Link to={{ pathname: '/review', state: { id, owner_id, yn_reply, review_title, modified_day, review_body, yn_visible } }}>
     /*   <img src={poster} alt={title} title={title} /> */
        <div className="review__data">
          <h3 className="review__title">{review_title}</h3>
          <h5 className="modified_day">{modified_day}</h5>
          <h5 className="review__title">{review_title}</h5>
          <ul className="review__owner__id">{owner_id}</ul>
          <p className="review__body">{summary.slice(0, 180)}...</p>
        </div>
      </Link>
    </div>
  );
}

Review.propTypes = {
    review_id: PropTypes.string.isRequired,
  review_title: PropTypes.string.isRequired,
  review_body: PropTypes.string.isRequired,
};

export default Review;
