*/

const SmartContractExecution = require('../smartContractExecution/smartContractExecution')

/**
 * Represents a smart contract execution transaction wrapper.
 * @class
 * @hideconstructor
 */
class SmartContractExecutionWrapper {
    /**
     * Creates a smart contract execution transaction wrapper.
     * The wrapper plays an intermediate role for calling static functions of each transaction type in `caver.transaction`.
     * @constructor
     * @param {object} _klaytnCall - An object includes klay rpc calls.
     */
    constructor(klaytnCall) {
        this.klaytnCall = klaytnCall
    }

    /**
     * Calls `SmartContractExecution.create`.
     *
     * @param {object} obj - An object defines fields to create a tx.
     * @return {string} An RLP-encoded transaction string.
     */
    create(obj) {
        return SmartContractExecution.create(obj, this.klaytnCall)
    }

    /**
     * Calls `SmartContractExecution.decode`.
     *
     * @param {string} encoded - RLP-encoded string.
     * @return {string} SmartContractExecution instance.
     */
    decode(encoded) {
        return SmartContractExecution.decode(encoded, this.klaytnCall)
    }
}

module.exports = SmartContractExecutionWrapper
