import React from 'react';
import axios from 'axios';
import Review from '../components/Review';
import WriteReview from '../components/WriteReview';
import './Detail.css';

class Detail extends React.Component {
  state = {
    isLoading: true,
    reviews: [],
  };

  getReviews = async (thiscontent_id) => {

    console.log('prev_id : ' + thiscontent_id);
    var myurl;
    if (thiscontent_id){
      myurl = 'https://8snn27pzii.execute-api.ap-northeast-2.amazonaws.com/web3studydbconn?content_id=' + thiscontent_id;
    }
    else{
      myurl = 'https://8snn27pzii.execute-api.ap-northeast-2.amazonaws.com/web3studydbconn?content_id=ALL';
    }
    const { data:reviews } = await axios.get(myurl); 
    console.log('myurl : ' + myurl);
    // 람다로 만든 API URL : 리뷰 리스트 업, content_id 에 해당하는 영화 아이디를 넣음

    this.setState({ reviews, isLoading: false });
  };

  componentDidMount() {
    const { location, history } = this.props;
    if (location.state === undefined || location.state.content_id === undefined ) {
      history.push('/');
    } 
    this.getReviews(location.state.content_id);
  }

  render() {

    const { location } = this.props;
    const { isLoading, reviews } = this.state;
    const numRows = reviews.length;

    console.log("content id:" + this.props.content_id);

    if (!location.state) {
      return null; // defense code have not mean.
    }

    if (isLoading) {
      return (
        <section className="reviewcontainer">
        <div className="loader">
          <span className="loader__text">Loading...</span>
        </div>
        </section>
      );
    }

    if (!numRows) {
      return (
      <section className="reviewcontainer">
         <div className="reviews">
         <span>{location.state.title}에 대해 첫리뷰를 작성해주세요</span>
         {
              WriteReview(location.state.content_id, "sohan", 0)
         }


        </div>  
      </section>
      );
    }

    return (
      <section className="reviewcontainer">
          <div className="reviews">
            { 
              WriteReview(location.state.content__id, "sohan", 0)
            }
            {
              reviews.map((review) => {
              return (
                <Review
                  key = {review.id}
                  id={review.content_id}
                  title={location.state.title}
                  owner_id={review.owner_id}
                  yn_reply={review.yn_reply}
                  review_title={review.review_title}
                  review_body={review.review_body}
                  modified_day={review.modified_day}
                  yn_visible={review.yn_visible}
                />
              );
            })}
          </div>
      </section> );
  }
}

export default Detail;
