import React, { useState, useEffect } from 'react';
import './Review.css';
import axios from 'axios';
import PropTypes from 'prop-types';

function Likedislike({content_id, review_id, owner_id}) {
   
    const [likecnt, setlikecnt] = useState(0);
    const [Dislikecnt, setDislikecnt] = useState(1);

    const loginID = owner_id;
    // const loginID = "testid";

     useEffect(async () => {
         const fetchData = async () => {

             console.log('content_id : ' + content_id + '  review_id :' + review_id + '  owner_id :' + owner_id);

             axios.post('https://vrkj60roca.execute-api.ap-northeast-2.amazonaws.com/Production',
                 {
                     "target": "getlike",
                     "content_id": content_id,
                     "review_id": review_id
                 })
                 .then((response) => {

                     const res = JSON.parse(response.data.body);
                     setlikecnt(likecnt => 0);
                     setDislikecnt(Dislikecnt => 0);

                     var key;

                     if (response.data.statusCode = 200 && res.length > 0) {
                         for (key in res) {
                             if (res[key].yn_like == true) {
                                 console.log("like : " + res[key].CNT);
                                 setlikecnt(likecnt => res[key].CNT);
                             }
                             else {

                                 console.log("dislike : " + res[key].CNT);
                                 setDislikecnt(Dislikecnt => res[key].CNT);
                             }

                         }
                     }
                 })
                 .catch((error) => {
                     console.log(error.response);
                 });
         }

         fetchData();

    }, []);

    const likeadd = () => {
        console.log('likeadd review_id:' + review_id);
        if(loginID !== owner_id){ setlikecnt(likecnt => likecnt + 1); } else {alert('내 리뷰에 내가 좋아요는 못합니다.!')}
    }

    const Dislikeadd = () => {
        console.log('Dislike review_id:' + review_id);
        if (loginID !== owner_id) { setDislikecnt(Dislikecnt => Dislikecnt + 1); } else { alert('내 리뷰에 내가 싫어요는 못합니다.!') }
        
    }

    return (
        <div className = "likedislike" > {review_id}
            <img src="./assets/iconmonstr-thumb-14.svg" alt="like" /><button onClick={likeadd}>{likecnt}</button>  
            <img src="./assets/iconmonstr-thumb-20.svg" alt="dislkie" /><button onClick={Dislikeadd}>{Dislikecnt}</button>
        </div>
    );
}

Likedislike.propTypes = {
    content_id: PropTypes.string.isRequired,
    review_id: PropTypes.number.isRequired,
    owner_id: PropTypes.string.isRequired
};

export default Likedislike;
