import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './Login.css';
import caver from 'caver-js'



const logindb = [
  {
    uname : "sohan1",
    password : "pass1"
  },
  {
    uname : "sohan2",
    password : "pass2"
  },
  {
    uname : "sohan3",
    password : "pass3"
  },
  {
    uname : "sohan4",
    password : "pass4"
  }
];

const errors = {
  uname: "invalide username",
  upass: "invalid password"
};

function Login(){
 /* 
state = {
  errorMessagees: '',
  isSubmitted:false,
  account:'',
  balance : 0,
  network :''
} */
  
const [errorMessages, setErrorMessages] = useState({});
const [isSubmiited, setIsSubmitted] = useState(false);


const loadAccountInfo = async () => {
  const { klaytn } = window

  if (klaytn) {
    try {
      await klaytn.enable()
      this.setAccountInfo(klaytn)
      klaytn.on('accountsChanged', () => this.setAccountInfo(klaytn))
    } catch (error) {
     // console.log('User denied account access : '+error) 
     console.log(error) 
    }
  } else {
    console.log('Non-Kaikas browser detected. You should consider trying Kaikas!')
  }
}

const setAccountInfo = async () => {
  const { klaytn } = window
  if (klaytn === undefined) return

  const account = klaytn.selectedAddress
  const balance = await caver.klay.getBalance(account)
  this.setState({
    account: account,
    balance: caver.utils.fromPeb(balance, 'KLAY'),
  })
}

const setNetworkInfo = () => {
  const { klaytn } = window
  if (klaytn === undefined) return

  this.setState({ network: klaytn.networkVersion })
  klaytn.on('networkChanged', () => this.setNetworkInfo(klaytn.networkVersion))
}

const handlekaikas = (event) => {
  event.preventDefault();
  
  const { klaytn } = window;

  this.loadAccountInfo();
  
};


const handleSubmit = (event) => {
  event.preventDefault();

  var {uname, upass} = document.forms[0];

  const userData = logindb.find((user) => user.uname === uname.value );

  if ( userData) {
    if(userData.password !== upass.value ) {
     //  setErrorMessages({name : "upass", message: errors.upass});
     this.setState({errrorMessages : {name : "upass", message: errors.upass}});
    } else {
     // setIsSubmitted(true);
      this.setState({IsSubmiited : true});
      sessionStorage.setItem("uname", userData.uname);
    }
  } else {
  //  setErrorMessages({name : "uname", message: errors.uname });
    this.setState({errorMessages: {name : "uname", message: errors.uname }});
  }
};

const renderErrorMessage = (name2) => name2 === this.state.errorMessages.name && (
  <div className="error">{this.state.errorMessages.message}</div>
);

const renderForm = (
    <div className="form">
    <form onSubmit = {this.handleSubmit}>
     <div className="input-container">
      <label>Nickname</label>
      <input type = "myid" id="uname" name="uname" required />
      {this.renderErrorMessage("uname")}s
     </div>
     <div className="input-container">
      <label>Password</label>
      <input type = "password" id = "upass" name="upass" required />
      {this.renderErrorMessage("upass")}
     </div>
     <div className="button-container">
      <input type = "submit" />
     </div>
      <div className="button-container">
          <input type="button" onClick={this.handlekaikas}/>
       </div>
    </form>
    </div>
  );

  return (
      <div className="login__container">
        <div className="mytitle">Log In</div>
       {this.state.isSubmiited ? <div>User is successfullly logged in</div> : this.renderForm}
      </div>
    );
}

export default Login;
