import React, { useState, useEffect } from 'react';
import './Review.css';
import axios from 'axios';
import PropTypes from 'prop-types';

function Likedislike({content_id, review_id, owner_id}) {

    const [likecnt, setlikecnt] = useState(0);
    const [Dislikecnt, setDislikecnt] = useState(1);

    useEffect(async () => {
        const fetchData = async () => {
            console.log('likecnt : '+ likecnt + '  Dislikecnt :' + Dislikecnt) ;
            console.log('content_id : ' + content_id + '  review_id :' + review_id + '  owner_id :' + owner_id);

            setlikecnt(likecnt => 0);
            setDislikecnt(Dislikecnt => 0);
            /*
            const res = await axios.get(...)
            if (res.data.type === 'liked') setLike(true) */
        }
        fetchData()
    }, []);

    const likeadd = () => {
        setlikecnt(likecnt => likecnt + 1);
    }

    const Dislikeadd = () => {
        setDislikecnt(Dislikecnt => Dislikecnt + 1);
    }

    return (
        <div className = "likedislike" > {review_id}
            <img src="./assets/iconmonstr-thumb-14.svg" alt="like" /><button onClick={likeadd}>{likecnt}</button>  
            <img src="./assets/iconmonstr-thumb-20.svg" alt="dislkie" /><button onClick={Dislikeadd}>{Dislikecnt}</button>
        </div>
    );
}

Likedislike.propTypes = {
    content_id: PropTypes.string.isRequired,
    review_id: PropTypes.number.isRequired,
    owner_id: PropTypes.string.isRequired
};

export default Likedislike;
