import React from 'react';
import PropTypes from 'prop-types';
import './Review.css';
import { Link } from 'react-router-dom';


function Review({ id, title, owner_id, yn_reply, review_title, modified_day, review_body, yn_visible }) {
  return (
    <div className="review">
          <div className="review__data"> 
          <h3 className="content__title">{title}</h3>
          <h3 className="review__title">{review_title}</h3>
          <ul className="review__owner__id">{owner_id}</ul>
          <ul className="content__id">{id}</ul>
          <p className="review__body">{review_body.slice(0, 2000)}...</p>
          <h5 className="modified_day">{modified_day}</h5>
        </div>
        <Link to={{ pathname: '/review', state: { id, title, owner_id, yn_reply, review_title, modified_day, review_body, yn_visible } }}> 
        <h5 className="content__id">좋아요 자리</h5>
        </Link>
    </div>
  );
}

/*

function WriteReview({ content_id, owner_id, reply_id }) {
  return (
       <div className="form_review_write">
      <Link to={{ pathname: '/review', state: { content_id, owner_id, reply_id, review_title, review_body } }}>
      <div className="review__data">
        <h3 className="content__title">{title}</h3>
        <h3 className="review__title"><span>리뷰제목</span><input name='review_title' ></input></h3>
        <ul className="review__owner__id">{owner_id}</ul>
        <p className="review__body"><span>내용</span><textarea name='review_body'></textarea></p>
      </div>
      </Link>
    </div>
  );
} */



Review.propTypes = {
  id: PropTypes.number.isRequired,
  title: PropTypes.string.isRequired,
  review_title: PropTypes.string.isRequired,
  review_body: PropTypes.string.isRequired,
};

export default Review;
