import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './Login.css';
import caver from 'caver-js'



const logindb = [
  {
    uname : "sohan1",
    password : "pass1"
  },
  {
    uname : "sohan2",
    password : "pass2"
  },
  {
    uname : "sohan3",
    password : "pass3"
  },
  {
    uname : "sohan4",
    password : "pass4"
  }
];

const errors = {
  uname: "invalide username",
  upass: "invalid password"
};

function Login(){
 /* 
state = {
  errorMessagees: '',
  isSubmitted:false,
  account:'',
  balance : 0,
  network :''
} */
  
const [errorMessages, setErrorMessages] = useState({});
const [isSubmiited, setIsSubmitted] = useState(false);
const [accnt, setAccnt] = useState("");
const [bal, setBal] = useState(0.0);
const [ntk, setNetwork] = useState("");


const loadAccountInfo = async () => {
  const { klaytn } = window

  if (klaytn) {
    try {
      await klaytn.enable()
      setAccountInfo(klaytn)
      klaytn.on('accountsChanged', () => setAccountInfo(klaytn))
    } catch (err) {
     // console.log('User denied account access : '+error) 
     console.log(err) 
    }
  } else {
    console.log('Non-Kaikas browser detected. You should consider trying Kaikas!')
  }
}

const setAccountInfo = async () => {
  const { klaytn } = window
  if (klaytn === undefined) return

  const myaccnt = klaytn.selectedAddress ;
  setAccnt(myaccnt);
  sessionStorage.setItem("accnt", klaytn.selectedAddress);
  // console.log('accnt:' + sessionStorage.getItem("accnt"));
  console.log('accnt:' + accnt);
  // const mycheck = await caver.klay.getBalance("0x5bdcdefed912981db7f5c6f7b67717b8144fd69f");
  const mycheck = await caver.klay.getBalance(accnt);
  console.log('balance:' + mycheck);

  setBal(caver.utils.fromPeb(mycheck,'KLAY'));

}

const setNetworkInfo = () => {
  const { klaytn } = window
  if (klaytn === undefined) return

  setNetwork(klaytn.networkVersion);
  sessionStorage.setItem("knetwork", ntk);
  klaytn.on('networkChanged', () => this.setNetworkInfo(klaytn.networkVersion))
}

const handlekaikas = (event) => {
  event.preventDefault();
  
  const { klaytn } = window;

  loadAccountInfo();
  
};


const handleSubmit = (event) => {
  event.preventDefault();

  var {uname, upass} = document.forms[0];

  const userData = logindb.find((user) => user.uname === uname.value );

  if ( userData) {
    if(userData.password !== upass.value ) {
     setErrorMessages({name : "upass", message: errors.upass});
     
    } else {
      setIsSubmitted(true);
      sessionStorage.setItem("uname", userData.uname);
    }
  } else {
    setErrorMessages({name : "uname", message: errors.uname });
  }
};

const renderErrorMessage = (name2) => name2 === errorMessages.name && (
  <div className="error">{errorMessages.message}</div>
);

const renderForm = (
    <div className="form">
    <form onSubmit = {handleSubmit}>
     <div className="input-container">
      <label>Nickname</label>
      <input type = "myid" id="uname" name="uname" required />
      {renderErrorMessage("uname")}s
     </div>
     <div className="input-container">
      <label>Password</label>
      <input type = "password" id = "upass" name="upass" required />
      {renderErrorMessage("upass")}
     </div>
     <div className="button-container">
      <input type = "submit" />
     </div>
      <div className="button-container">
          <input type="button" onClick={handlekaikas}/>
       </div>
    </form>
    </div>
  );

  return (
      <div className="login__container">
        <div className="mytitle">Log In</div>
       {isSubmiited ? <div>User is successfullly logged in</div> : renderForm}
      </div>
    );
}

export default Login;
