import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import './Review.css';
import Axios from 'axios';
// import { Link } from 'react-router-dom';

const submitReview = () => {
    Axios.post('https://vrkj60roca.execute-api.ap-northeast-2.amazonaws.com/Production',
        {
            "target": "review",
            "content_id": "99",
            "reply_id": "0",
            "owner_id": "sohan",
            "review_title": "submitReview",
            "review_body": "body _ write 99 body body body 가가가 나나나"
        }).then(
            ()=>{alert('등록완료');}
        )
};


function WriteReview({ content_id, owner_id, reply_id }) {
    console.log("content_id :" + content_id + ", owner_id :" + owner_id);
    return (
        <div className="form_review_write">
          <h5 className="content_title" >리뷰작성:{content_id}</h5>
          <h5 className="content_title" >{owner_id}</h5>
          <input className = "title_input" type="text" placeholder="제목"></input>
          <input className = "body_input" type="text" placeholder="내용" ></input>
          <p></p>
          <button className="submit_button" value="글쓰기" onClick={submitReview}>글쓰기</button>
          <button className="go_list_button" value="목록으로">목록으로</button>
        </div>
    );
}

