import React from 'react';
import axios from 'axios';
import Review from '../components/Review';
import './Detail.css';

class Detail extends React.Component {
  state = {
    isLoading: true,
    reviews: [],
  };

  getReviews = async () => {
   /* const {
      data: { reviews },
    } = await axios.get('https://8snn27pzii.execute-api.ap-northeast-2.amazonaws.com/web3studydbconn?content_id=1'); 
    */
    // 람다로 만든 API URL : 리뷰 리스트 업, content_id 에 해당하는 영화 아이디를 넣음

    const reviews =[{
  "id": 1,
  "content_id": 0,
  "reply_id": 0,
  "owner_id": "sohan",
  "yn_reply": 0,
  "review_title": "title_test",
  "review_body": "review_body_test",
  "modified_day": "2022-07-12T08:59:31.000Z",
  "yn_visible": 1
},
  {
    "id": 2,
    "content_id": 0,
    "reply_id": 0,
    "owner_id": "sohan",
    "yn_reply": 0,
    "review_title": "title_test2",
    "review_body": "review_body_test22",
    "modified_day": "2022-07-12T08:59:47.000Z",
    "yn_visible": 1
  },
  {
    "id": 3,
    "content_id": 0,
    "reply_id": 0,
    "owner_id": "sohan",
    "yn_reply": 0,
    "review_title": "title_test3",
    "review_body": "리뷰 테스트트트",
    "modified_day": "2022-07-18T01:56:20.000Z",
    "yn_visible": 1
  }]; 
    console.log('reviews data body : ' + reviews);
    this.setState({ reviews, isLoading: false });
  };


  componentDidMount() {
    /* const { location, history } = this.props;
    if (location.state === undefined) {
      history.push('/');
    } */

    this.getReviews();
  }

  render() {
  //  console.log("componentDidMout" + this.props);
  //  const { location } = this.props;
    const { isLoading, reviews } = this.state;

    return (
      <section className="reviewcontainer">
        {isLoading ? (
          <div className="loader">
            <span className="loader__text">Loading...</span>
          </div>
        ) : (
          <div className="reviews">
            {reviews.map((review) => {
              return (
                <Review
                  key = {review.id}
                  id={review.id}
                  owner_id={review.owner_id}
                  yn_reply={review.yn_reply}
                  review_title={review.review_title}
                  review_body={review.review_body}
                  modified_day={review.modified_day}
                  yn_visible={review.yn_visible}
                />
              );
            })}
          </div>
        )}
      </section>
    );
  /*
    if (location.state) {
      return (
        <section className="reviewcontainer">
        <div className="movies">
        <span>{location.state.id},{location.state.title}</span>
        </div>
        </section>
      );
    } else {
      return null;
    } */
  } 
}

export default Detail;
